import React, { useState } from 'react';
import './CreatePost.css';
import { supabase } from '../client';

const CreatePost = () => {
    // Initialize state
    const [post, setPost] = useState({ title: '', author: '', description: '' });

    // Handle input changes
    const handleChange = (event) => {
        const { name, value } = event.target;
        setPost((prev) => ({
            ...prev,
            [name]: value,
        }));
    };

    // Handle form submission
    const createPost = async (event) => {
        event.preventDefault(); // Prevent default form submission

        // Insert data into the Posts table using Supabase and select the inserted data
        await supabase
            .from('Posts')
            .insert({
                title: post.title,
                author: post.author,
                description: post.description,
            })
            .select();

        // Redirect to home page
        window.location = '/';
    };

    return (
        <div>
            <form>
                <label htmlFor="title">Title</label> <br />
                <input type="text" id="title" name="title" value={post.title} onChange={handleChange} /> <br />
                <br/>

                <label htmlFor="author">Author</label> <br />
                <input type="text" id="author" name="author" value={post.author} onChange={handleChange} /> <br />
                <br/>

                <label htmlFor="description">Description</label> <br />
                <textarea rows="5" cols="50" id="description" name="description" value={post.description} onChange={handleChange}></textarea> <br />
                <br/>

                {/* Submit button with onClick event handler */}
                <input type="submit" value="Submit" onClick={createPost} />
            </form>
        </div>
    );
};

export default CreatePost;
