import React, { useState, useEffect } from 'react';
import { useParams, useNavigate } from 'react-router-dom';
import { supabase } from '../client';
import './EditPost.css';

const EditPost = ({ data }) => {
    const { id } = useParams();
    const navigate = useNavigate(); // Use navigate from react-router-dom

    const [post, setPost] = useState({ id: null, title: "", author: "", description: "" });

    // Fetch the post details when the component mounts
    useEffect(() => {
        const fetchPost = () => {
            const existingPost = data.find(post => post.id === id);
            if (existingPost) {
                setPost(existingPost);
            }
        };
        fetchPost();
    }, [id, data]);

    // Handle input changes
    const handleChange = (event) => {
        const { name, value } = event.target;
        setPost((prev) => ({
            ...prev,
            [name]: value,
        }));
    };

    // Update the post in the database
    const updatePost = async (event) => {
        event.preventDefault();

        // Perform the update operation using Supabase
        await supabase
            .from('Posts')
            .update({ title: post.title, author: post.author, description: post.description })
            .eq('id', id);

        // Redirect to home page
        navigate('/');
    };

    // Delete the post from the database
    const deletePost = async (event) => {
        event.preventDefault();

        // Perform the delete operation using Supabase
        await supabase
            .from('Posts')
            .delete()
            .eq('id', id);

        // Redirect to home page
        navigate('/');
    };

    return (
        <div>
            <form onSubmit={updatePost}>
                <label htmlFor="title">Title</label>
                <br />
                <input type="text" id="title" name="title" value={post.title} onChange={handleChange} />
                <br /><br />

                <label htmlFor="author">Author</label>
                <br />
                <input type="text" id="author" name="author" value={post.author} onChange={handleChange} />
                <br /><br />

                <label htmlFor="description">Description</label>
                <br />
                <textarea rows="5" cols="50" id="description" name="description" value={post.description} onChange={handleChange}></textarea>
                <br /><br />

                <input type="submit" value="Submit" />
                <button className="deleteButton" onClick={deletePost}>Delete</button>
            </form>
        </div>
    );
};

export default EditPost;
