import React, { useState, useEffect } from 'react';
import Card from '../components/Card';
import { supabase } from '../client';

const ReadPosts = (props) => {
    const [posts, setPosts] = useState([]);

    useEffect(() => {
        // Define the fetchPosts function
        const fetchPosts = async () => {
            // Fetch all entries from the Posts table
            const { data } = await supabase
                .from('Posts')
                .select()
                .order('created_at', { ascending: true }); // Order the data by 'created_at' in ascending order

            // Update the state of posts
            setPosts(data);
        };

        // Call fetchPosts to fetch the data from the database
        fetchPosts();
    }, []);

    return (
        <div className="ReadPosts">
            {posts && posts.length > 0 ? (
                posts.map((post) => (
                    <Card
                        key={post.id}
                        id={post.id}
                        title={post.title}
                        author={post.author}
                        description={post.description}
                    />
                ))
            ) : (
                <h2>No Challenges Yet ðŸ˜ž</h2>
            )}
        </div>
    );
};

export default ReadPosts;
