import React from 'react';
import './Card.css';
import { useState } from 'react';
import more from './amongus1.png'; // Import the PNG image

const Card = (props) => {
  const [count, setCount] = useState(0);
  const updateCount = () => {
    setCount((count) => count + 1);
  };

  return (
    <div className="Card">
      <img className="moreButton" src={more} alt="more button" />
      <p>{'amongus1.png'}</p> {/* Display the filename as a string */}
      <h2 className="name">{props.name}</h2>
      <p className="speed">Speed: {props.speed}</p>
      <p className="color">Color: {props.color}</p>
      <button className="upVoteButton" onClick={updateCount}>
        Up Vote Crewmate! {count}
      </button>
    </div>
  );
};

export default Card;
