// App.js

import './App.css';
import React, { useState, useEffect } from 'react';
import { useRoutes } from 'react-router-dom';
import ReadPosts from './pages/ReadPosts';
import CreatePost from './pages/CreatePost';
import EditPost from './pages/EditPost';
import { Link } from 'react-router-dom';
import { supabase } from './client'; // Import Supabase client

const App = () => {
  const [crewmates, setCrewmates] = useState([]);

  useEffect(() => {
    const fetchCrewmates = async () => {
      try {
        const { data, error } = await supabase.from('crewmates').select('*');
        if (error) {
          throw error;
        }
        setCrewmates(data);
      } catch (error) {
        console.error('Error fetching crewmates:', error.message);
      }
    };

    fetchCrewmates();
  }, []);

  const element = useRoutes([
    {
      path: '/',
      element: <ReadPosts crewmates={crewmates} />,
    },
    {
      path: '/edit/:id',
      element: <EditPost />,
    },
    {
      path: '/new',
      element: <CreatePost />,
    },
  ]);

  return (
    <div className="App">
      <div className="header">
        <h1>Crewmates!</h1>
        <Link to="/"><button className="headerBtn"> Explore Crewmates</button></Link>
        <Link to="/new"><button className="headerBtn"> Submit Crewmate</button></Link>
      </div>
      {element}
    </div>
  );
};

export default App;
